function showChess() {
  

    for (var MOC of allChess) {
        if (MOC.style.backgroundImage == '' || MOC.style.backgroundImage == 'none') {
            var randomNumber = Math.floor(Math.random() * 6) + 1
            console.log(randomNumber)
            switch(randomNumber) {
                case 1: {
                    
                    MOC.style.backgroundImage = star;
                    break;
                }
                case 2: {
                    MOC.style.backgroundImage = sword;
                    break;
                }
                case 3: {
                    MOC.style.backgroundImage = heart;
                    break;
                }
                case 4: {
                    MOC.style.backgroundImage = water;
                    break;
                }
                case 5: {
                    MOC.style.backgroundImage = melon;
                    break;
                }
                case 6: {
                    MOC.style.backgroundImage = yingyang;
                    break;
                }
            }
        MOC.classList.add("backgroundSize")
        focus()
        }
    }
     
    

}
